import React from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';

const options = [
  { label: 'üìä Analisis Data Tanaman' },
  { label: 'ü§ñ Dapatkan Rekomendasi Berbasis AI' },
  { label: 'üå§Ô∏è Cek Prakiraan Cuaca' },
  { label: 'üìö Jelajahi Wawasan Pertanian' },
  { label: 'üßë‚Äçüåæ Tanya Saran Ahli' },
  { label: '‚ôªÔ∏è Pelajari Praktik Berkelanjutan' },
];

export default function HomeScreen() {
  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Image source={require('../../assets/cornai-logo.png')} style={styles.logo} />
      <Text style={styles.message}>
        Halo! üåΩ Saya CornAI. Bagaimana saya bisa mendukung perjalanan pertanian berkelanjutan Anda hari ini? Silakan pilih salah satu opsi berikut:
      </Text>
      {options.map((option, index) => (
        <TouchableOpacity key={index} style={styles.button}>
          <Text style={styles.buttonText}>{option.label}</Text>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  logo: {
    width: 120,
    height: 120,
    resizeMode: 'contain',
    marginBottom: 20,
    borderRadius: 60,
  },
  message: {
    textAlign: 'center',
    fontSize: 16,
    marginBottom: 20,
    color: '#333',
  },
  button: {
    backgroundColor: '#e0f2f1',
    padding: 15,
    borderRadius: 10,
    marginVertical: 8,
    width: '100%',
  },
  buttonText: {
    fontSize: 16,
    textAlign: 'center',
    color: '#00695c',
  },
});
